    private String getRandomDate(){
        GregorianCalendar gc = new GregorianCalendar();

        int year = this.getRandomInteger(2001, 2019);

        gc.set(gc.YEAR, year);

        int dayOfYear = this.getRandomInteger(1, gc.getActualMaximum(gc.DAY_OF_YEAR));

        gc.set(gc.DAY_OF_YEAR, dayOfYear);

        SimpleDateFormat formatForDateNow  = new SimpleDateFormat("dd-MM-yyyy");
        String dateFormat = formatForDateNow.format(gc.getTime());
        return dateFormat;
    }

    private void fillMessages(List<PhoneContacts> phoneContacts, List<PhoneCallMessage> phoneMessages){


        for( PhoneContacts contact : phoneContacts ){
            String contactPhone = contact.getPhoneNumber();
            int randomStarter = getRandomInteger(0, this.messagesCount / 2); // Random starter for different message count
            while( randomStarter++ < this.messagesCount ){
                PhoneCallMessage pcm = new PhoneCallMessage(contactPhone, getRandomCitation(), getRandomDate(), getRandomCallType(), getRandomMessageStatus());
                phoneMessages.add(pcm);
            }
        }
    }

    private void fillLogs(List<PhoneContacts> phoneContacts, List<PhoneCallLog> callLogs){
        for( PhoneContacts contact : phoneContacts ){
            int starter = getRandomInteger(0, this.logsCount / 2);

            while( starter++ < this.logsCount ){
                PhoneCallLog singleLog = new PhoneCallLog(contact.getPhoneNumber(), getRandomPhone(), getRandomCallType(), getRandomCallStatus(), getRandomDate());
                callLogs.add(singleLog);
            }
        }
    }

    /**
     *
     * @param phoneContacts an empty Collection for contacts
     * @param phoneMessages an empty Collection for messages
     * @param phoneLogs an empty Collection for logs
     */
    public void generate(List<PhoneContacts> phoneContacts, List<PhoneCallMessage> phoneMessages, List<PhoneCallLog> phoneLogs){
        fillContacts(phoneContacts);
        fillMessages(phoneContacts, phoneMessages);
        fillLogs(phoneContacts, phoneLogs);
        getRandomDate();
    }
